import React,{useState} from 'react';
import classes from './QueryPage.module.css';

const QueryPage = () => {
    const [askquery,setAskQuery] = useState("");
    const handleChange = (data)=>{
        setAskQuery(data);
    }

    return (
        <div className={classes.container}>
            <div className={classes.queryTitle}>
                Query Counter
            </div>
            <div className={classes.askQuery}>
                <div className={classes.ask_query_title}>
                    Ask Your Query
                </div>

                <textarea className={classes.ask_query_text_area} value={askquery} onChange={(e) => handleChange(e.target.value)} rows="10" placeholder="Type your query here">

                </textarea>
            </div>
        </div>
    );

}

export default QueryPage;
