import React, { useState } from "react";
import classes from "./AskQuery.module.css";

function ValidationMessage(props) {
    if (!props.valid) {
        return (
            <div className={classes.error_msg}>
                <p>{props.message}</p>
            </div>
        );
    }
    return null;
}

const AskQuery = (props) => {
    const kidlogo = process.env.PUBLIC_URL + "/images/query.svg";
    const [username, setusername] = useState("");
    const [usernameValid, setUsernameValid] = useState(false);
    const [category, setCategory] = useState("");
    const [categoryValid, setCategoryValid] = useState(false);
    const [query, setQuery] = useState("");
    const [queryValid, setQueryValid] = useState(false);
    const [formValid, setFormValid] = useState(false);
    const [errorMessage, setErrorMessage] = useState({});

    const validateForm = () => {
        setFormValid(usernameValid && categoryValid && queryValid);
    };
    const updateUsername = (data) => {
        let errorMsg = { ...errorMessage };
        setusername(data);
        if (data.length > 0) {
            setUsernameValid(true);
        } else {
            setUsernameValid(false);
            errorMsg.username = "Name is required!";
            setErrorMessage(errorMsg);
        }
        validateForm();
    };

    const updateCategory = (data) => {
        let errorMsg = { ...errorMessage };
        setCategory(data);
        if (data.length > 0) {
            setCategoryValid(true);
        } else {
            setCategoryValid(false);
            errorMsg.category = "Category is required";
            setErrorMessage(errorMsg);
        }
        validateForm();
    };

    const updateQuery = (data) => {
        let errorMsg = { ...errorMessage };
        setQuery(data);
        if (data.length > 0) {
            setQueryValid(true);
        } else {
            setQueryValid(false);
            errorMsg.query = "Query is required";
            setErrorMessage(errorMsg);
        }
        validateForm();
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const url = "";

        fetch(url, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
            },
        });
    }

    return (
        <div className={classes.main}>
            <div
                className={classes.container}
                style={{
                    backgroundImage: `url(${
                        process.env.PUBLIC_URL + "/images/CurveBackground.svg"
                    })`,
                }}
            >
                <div className={classes.item1}>
                    <h2>Ask Query</h2>
                    <p>
                        Do you have any question regarding any Events or
                        anything to ask from the Event Manager?
                    </p>
                    <img src={kidlogo} alt="creative"></img>
                </div>
                <div className={classes.item2}>
                    <form action="#" id="js-form" className={classes.form}>
                        {/* <div className={classes.form_group}>
                            <label className={classes.label} htmlFor="username">
                                Username
                            </label>

                        </div> */}
                        <div className={classes.form_group}>
                            <label className={classes.label} htmlFor="username">
                                Username
                            </label>
                            <ValidationMessage
                                valid={usernameValid}
                                message={errorMessage.username}
                            />
                            <textarea
                                type="text"
                                id="username"
                                name="username"
                                className={classes.form_field}
                                value={username}
                                onChange={(e) => updateUsername(e.target.value)}
                                placeholder="Username"
                            />
                        </div>
                        <div className={classes.form_group}>
                            <label className={classes.label} htmlFor="category">
                                Query Category
                            </label>
                            <ValidationMessage
                                valid={categoryValid}
                                message={errorMessage.category}
                            />
                            <textarea
                                type="text"
                                id="category"
                                name="category"
                                className={classes.form_field}
                                value={category}
                                onChange={(e) => updateCategory(e.target.value)}
                                placeholder="Query Category"
                            />
                        </div>
                        <div className={classes.form_group}>
                            <label className={classes.label} htmlFor="query">
                                Query
                            </label>
                            <ValidationMessage
                                valid={queryValid}
                                message={errorMessage.query}
                            />
                            <textarea
                                type="text"
                                id="query"
                                name="query"
                                className={classes.form_text_field}
                                value={query}
                                onChange={(e) => updateQuery(e.target.value)}
                                rows="5"
                                column="10"
                                placeholder="Enter your query here"
                            />
                        </div>
                        <div className="form-controls">
                            <button
                                className={classes.btn}
                                type="submit"
                                disabled={!formValid}
                            >
                                Ask Query
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};
export default AskQuery;
